import { Component } from '@angular/core';

@Component({
  selector: 'app-sms-sent-history',
  templateUrl: './sms-sent-history.component.html',
  styleUrls: ['./sms-sent-history.component.scss'],
})
export class SmsSentHistoryComponent {
  // Add properties and logic to display SMS sent history
}
<div class="sms-sent-history-container">
  <h2>SMS Sent History</h2>
  <ul>
    <li *ngFor="let sms of sentHistory">
      <div><strong>Phone Number:</strong> {{ sms.phoneNumber }}</div>
      <div><strong>Date:</strong> {{ sms.date | date : "medium" }}</div>
      <div><strong>Message:</strong> {{ sms.message }}</div>
      <div><strong>Message Length:</strong> {{ sms.message.length }}</div>
    </li>
  </ul>
</div>
