import { Component } from '@angular/core';

@Component({
  selector: 'app-sms-form',
  templateUrl: './sms-form.component.html',
  styleUrls: ['./sms-form.component.scss'],
})
export class SmsFormComponent {
  phoneNumber: string = '';
  message: string = '';

  submitSmsForm() {
    if (this.phoneNumber && this.message) {
      // Handle SMS submission logic here
      console.log('SMS Submitted:', this.phoneNumber, this.message);

      // Clear form fields
      this.phoneNumber = '';
      this.message = '';
    }
  }
}
<div class="sms-form-container">
  <h2>SMS Submission Form</h2>
  <form (ngSubmit)="submitSmsForm()">
    <label for="phoneNumber">Phone Number:</label>
    <input
      type="text"
      id="phoneNumber"
      name="phoneNumber"
      required
      [(ngModel)]="phoneNumber"
    />

    <label for="message">Message:</label>
    <textarea
      id="message"
      name="message"
      rows="4"
      required
      [(ngModel)]="message"
    ></textarea>

    <button type="submit">Submit SMS</button>
  </form>
</div>
