import { Component } from '@angular/core';
import { DashboardApiService } from '../dashboard-api.service';

@Component({
  selector: 'app-sms-form',
  templateUrl: './sms-form.component.html',
  styleUrls: ['./sms-form.component.scss'],
})
export class SmsFormComponent {
  phoneNumber: string = '';
  message: string = '';

  constructor(private apiService: DashboardApiService) {} // Inject your API service

  submitSmsForm() {
    if (this.phoneNumber && this.message) {
      // Handle SMS submission logic here
      this.apiService.submitSms(this.phoneNumber, this.message).subscribe(
        (response) => {
          console.log('SMS Submitted:', response);
          this.message = '';
        },
        (error) => {
          console.error('Error submitting SMS:', error);
        }
      );
    }
  }
}
<div class="sms-form-container">
  <form (ngSubmit)="submitSmsForm()" class="form-group">
    <label for="phoneNumber">Phone Number:</label>
    <input
      type="text"
      id="phoneNumber"
      name="phoneNumber"
      required
      [(ngModel)]="phoneNumber"
    />

    <label for="message">Message:</label>
    <textarea
      id="message"
      name="message"
      rows="4"
      required
      [(ngModel)]="message"
    ></textarea>

    <button type="submit">Submit SMS</button>
  </form>
</div>

<div class="sms-form">
  <div class="form-container">
    <div class="form-group">
      <label for="phoneNumber">Phone Number:</label>
      <input
        type="text"
        id="phoneNumber"
        name="phoneNumber"
        [(ngModel)]="phoneNumber"
      />
    </div>
    <div class="form-group">
      <label for="message">Message:</label>
      <textarea
        id="message"
        name="message"
        rows="4"
        [(ngModel)]="message"
      ></textarea>
    </div>
    <button type="submit">Submit</button>
  </div>
</div>
