import { Router } from '@angular/router';
import { Component } from '@angular/core';
import { AuthService } from './auth.service'; // Import the AuthService
import { catchError } from 'rxjs/operators';
import { throwError } from 'rxjs';

@Component({
  selector: 'app-auth',
  templateUrl: './auth.component.html',
  styleUrls: ['./auth.component.scss'],
})
export class AuthComponent {
  isSignIn: boolean = true;

  // Properties for form data
  signInEmail: string = '';
  signInPassword: string = '';
  signUpEmail: string = '';
  signUpPassword: string = '';
  confirmPassword: string = '';

  constructor(private authService: AuthService, private router: Router) {} // Inject the AuthService

  toggleForm() {
    this.isSignIn = !this.isSignIn;
  }

  signIn(signInForm: any) {
    if (signInForm.valid) {
      this.authService
        .signIn(this.signInEmail, this.signInPassword)
        .pipe(
          catchError((error) => {
            console.error('Sign In Error:', error);
            return throwError(error);
          })
        )
        .subscribe((response) => {
          console.log('Sign In Successful:', response);
          this.router.navigate(['/dashboard']);
        });
    }
  }

  signUp(signUpForm: any) {
    if (signUpForm.valid) {
      if (this.signUpPassword === this.confirmPassword) {
        this.authService
          .signUp(this.signUpEmail, this.signUpPassword)
          .pipe(
            catchError((error) => {
              console.error('Sign Up Error:', error);
              return throwError(error);
            })
          )
          .subscribe((response) => {
            console.log('Sign Up Successful:', response);
            this.router.navigate(['/dashboard']);
          });
      } else {
        alert('Passwords do not match.');
      }
    }
  }
}
<div class="auth-container">
  <div *ngIf="isSignIn">
    <h2>Sign In</h2>
    <form #signInForm="ngForm" (ngSubmit)="signIn(signInForm)">
      <label for="email">Email:</label>
      <input
        type="email"
        id="email"
        name="email"
        required
        [(ngModel)]="signInEmail"
      />

      <label for="password">Password:</label>
      <input
        type="password"
        id="password"
        name="password"
        required
        [(ngModel)]="signInPassword"
      />

      <button type="submit">Sign In</button>
    </form>
  </div>
  <div *ngIf="!isSignIn">
    <h2>Sign Up</h2>
    <form #signUpForm="ngForm" (ngSubmit)="signUp(signUpForm)">
      <label for="signup-email">Email:</label>
      <input
        type="email"
        id="signup-email"
        name="signup-email"
        required
        [(ngModel)]="signUpEmail"
      />

      <label for="signup-password">Password:</label>
      <input
        type="password"
        id="signup-password"
        name="signup-password"
        required
        [(ngModel)]="signUpPassword"
      />

      <label for="confirm-password">Confirm Password:</label>
      <input
        type="password"
        id="confirm-password"
        name="confirm-password"
        required
        [(ngModel)]="confirmPassword"
      />

      <button type="submit">Sign Up</button>
    </form>
  </div>
  <button (click)="toggleForm()">
    {{ isSignIn ? "Switch to Sign Up" : "Switch to Sign In" }}
  </button>
</div>
