import { Component } from '@angular/core';

@Component({
  selector: 'app-sms-submission-form',
  templateUrl: './sms-submission-form.component.html',
  styleUrls: ['./sms-submission-form.component.scss'],
})
export class SmsSubmissionFormComponent {
  phoneNumber: string = '';
  message: string = '';

  submitSmsForm() {
    if (this.phoneNumber && this.message) {
      // Handle SMS submission logic here
      console.log('SMS Submitted:', this.phoneNumber, this.message);

      // Clear form fields
      this.phoneNumber = '';
      this.message = '';
    }
  }
}
